import React from "react";
import { Form, Input, InputNumber, AutoComplete } from "antd";

const pokemonTypes = {
  1: "fire",
  2: "water",
  3: "grass",
  4: "poison",
  5: "flying",
  6: "dragon",
  7: "psychic",
  8: "steel",
  9: "normal",
  10: "rock",
  11: "ice",
};

export default (props: {
  setType: Function;
  setName: Function;
  setLimit: Function;
  limit: Number;
  name: String;
  type: String;
}) => {
  return (
    <Form
      className="Options"
      layout="inline"
      onFieldsChange={(values) => {
        console.log(values);
      }}>
      <Form.Item
        label="Pokemons per load"
        style={{ marginBottom: 0, marginRight: "20px" }}>
        <InputNumber
          placeholder="Limit"
          onChange={(e) => {
            props.setLimit(e);
          }}></InputNumber>
      </Form.Item>
      <Form.Item
        label="Search by name"
        style={{ marginBottom: 0, marginRight: "20px" }}>
        <Input
          placeholder="Name"
          onChange={(e) => {
            props.setName(e.target.value);
            e.preventDefault();
          }}></Input>
      </Form.Item>
      <Form.Item label="Search by type" style={{ marginBottom: 0 }}>
        <AutoComplete options={pokemonTypes}>
          <Input
            placeholder="Type"
            onChange={(e) => {
              props.setType(e.target.value);
              e.preventDefault();
            }}></Input>
        </AutoComplete>
      </Form.Item>
    </Form>
  );
};
