import React from "react";
import { Form, Input, InputNumber, AutoComplete } from "antd";

const pokemonTypes = [
  "fire",
  "water",
  "grass",
  "poison",
  "flying",
  "dragon",
  "psychic",
  "steel",
  "normal",
  "rock",
];

export default (props: {
  setType: Function;
  setName: Function;
  setLimit: Function;
  limit: Number;
  name: String;
  type: String;
}) => {
  return (
    <Form
      className="Options"
      layout="inline"
      onFieldsChange={(values) => {
        console.log(values);
      }}>
      <Form.Item
        label="Pokemons per load"
        style={{ marginBottom: 0, marginRight: "20px" }}>
        <InputNumber
          placeholder="Limit"
          onChange={(e) => {
            props.setLimit(e);
          }}></InputNumber>
      </Form.Item>
      <Form.Item
        label="Search by name"
        style={{ marginBottom: 0, marginRight: "20px" }}>
        <Input
          placeholder="Name"
          onChange={(e) => {
            props.setName(e.target.value);
            e.preventDefault();
          }}></Input>
      </Form.Item>
      <Form.Item label="Search by type" style={{ marginBottom: 0 }}>
        <Input
          placeholder="Type"
          onChange={(e) => {
            props.setType(e.target.value);
            e.preventDefault();
          }}></Input>
      </Form.Item>
    </Form>
  );
};
