import gql from "graphql-tag";

// base query
export const ALL_POKEMONS = gql`
  query Pokemons {
    pokemons {
      edges {
        node {
          id
          name
          types
          classification
        }
      }
      pageInfo {
        endCursor
        hasNextPage
      }
    }
  }
`;

// query by name

export const POKEMON_BY_NAME = gql`
  query Pokemons($name: String) {
    pokemons(edges: { node: { name: $name } }) {
      edges {
        node {
          id
          name
          types
          classification
        }
      }
      pageInfo {
        endCursor
        hasNextPage
      }
    }
  }
`;

export const POKEMONS_BY_TYPE = gql`
  query Pokemons($name: String) {
    pokemons(edges: { node: { name: $name } }) {
      edges {
        node {
          id
          name
          types
          classification
        }
      }
      pageInfo {
        endCursor
        hasNextPage
      }
    }
  }
`;
