import React, { useState } from "react";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";

const ALL_POKEMONS = gql`
  pokemons(limit: 15) {
    edges {
      node{
        id
      }
    }
    pageInfo{
      endCursor
      hasNextPage
    }
  }
`;

export default () => {
  const { loading, error, data } = useQuery(ALL_POKEMONS);
  console.log(loading, error, data);
  return <div />;
};
