import React from "react";
import Options from "./Options";
import { PageHeader } from "antd";

export default () => {
  const [state, setState] = useState({
    name: "",
    type: "",
  });
  const { loading, error, data } = useQuery(ALL_POKEMONS, {
    variables: {
      name: state.name,
    },
  });
  const { setType, setName } = props;
  return (
    <div className="Page">
      <PageHeader
        title="My Pokedex"
        ghost={false}
        extra={
          <Options
            setType={(type: string) => setType(type)}
            setName={(name: string) => setName(name)}
            name={props.name}
            type={props.name}></Options>
        }></PageHeader>
    </div>
  );
};
