import React, { useState } from "react";
import Options from "../presentational/Options";
import { PageHeader } from "antd";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";
import PokemonList from "../presentational/PokemonList";

export default () => {
  const [state, setState] = useState({
    name: "",
    type: "",
    limit: 150,
  });
  const { loading, error, data } = useQuery(ALL_POKEMONS, {
    variables: {
      name: state.name,
      type: state.type,
      limit: state.limit,
    },
    fetchPolicy: "cache-and-network",
  });
  console.log(loading, error, data);

  return (
    <div className="Page">
      <PageHeader
        title="My Pokedex"
        ghost={false}
        extra={
          <Options
            setType={(type: string) => setState({ ...state, type })}
            setName={(name: string) => setState({ ...state, name })}
            setLimit={(limit: number) => setState({ ...state, limit })}
            name={state.name}
            type={state.type}
            limit={state.limit}></Options>
        }></PageHeader>
      <PokemonList
        name={state.name}
        type={state.type}
        limit={state.limit}></PokemonList>
      )}
    </div>
  );
};
