import React, { useState } from "react";
import "./App.css";
import Main from "./components/containers/main";

function App() {
  const [appStarted, startApp] = useState(false);
  return (
    <div className="App">
      {appStarted ? (
        <div />
      ) : (
        <>
          <h1>My Pokedex</h1>
          <button onClick={() => startApp(true)}>EXPLORE</button>
        </>
      )}
    </div>
  );
}

export default App;
