import React, { useState } from "react";
import Options from "./Options";
import { PageHeader } from "antd";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";
import PokemonList from "../presentational/PokemonList";

const ALL_POKEMONS = gql`
  query Pokemons($name: String) {
    pokemons(q: $name) {
      edges {
        node {
          id
          name
          types
          classification
        }
      }
      pageInfo {
        endCursor
        hasNextPage
      }
    }
  }
`;

export default () => {
  const [state, setState] = useState({
    name: "",
    type: "",
  });
  const { loading, error, data } = useQuery(ALL_POKEMONS, {
    variables: {
      name: state.name,
    },
  });
  console.log(loading, error, data);

  return (
    <div className="Page">
      <PageHeader
        title="My Pokedex"
        ghost={false}
        extra={
          <Options
            setType={(type: string) => setState({ ...state, type })}
            setName={(name: string) => setState({ ...state, name })}
            name={state.name}
            type={state.type}></Options>
        }></PageHeader>
    </div>
  );
};
