import React, { useState, useReducer } from "react";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";
import Header from "./Header";
import PokemonList from "../presentational/PokemonList";

// base query
const ALL_POKEMONS = gql`
  query pokemons {
    pokemons(limit: 15) {
      edges {
        node {
          id
          name: types
          classification
        }
      }
      pageInfo {
        endCursor
        hasNextPage
      }
    }
  }
`;

export default () => {
  const [state, setState] = useState({
    name: "",
    type: "",
  });
  const { loading, error, data } = useQuery(ALL_POKEMONS);
  console.log(loading, error, data);
  if (loading) {
    return <div>Please wait, we are loading the data...</div>;
  } else {
    if (
      data &&
      data.pokemons &&
      data.pokemons.edges &&
      data.pokemons.edges.length
    ) {
      return (
        <>
          <Header
            setType={(type: string) => setState({ ...state, type })}
            setName={(name: string) => setState({ ...state, name })}></Header>
          <PokemonList list={data.pokemons.edges}></PokemonList>
        </>
      );
    } else {
      return <div>Sorry, something broke off. Please reload to try again!</div>;
    }
  }
};
