import React from "react";
import { List } from "antd";
import {
  PokemonNode,
  PokemonItem,
  Connection,
} from "../../typescript/interfaces";
import PokemonListItem from "./PokemonListItem";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";
import { ALL_POKEMONS, POKEMONS_BY_TYPE } from "../../graphql/queries";

export default (props: {
  name: string;
  type: string;
  limit: number;
  pageQuantity: number;
}) => {
  const {
    loading,
    error,
    data: genericData,
    fetchMore: genericFetchMore,
  } = useQuery(ALL_POKEMONS, {
    variables: {
      name: props.name,
      limit: props.limit,
    },
    skip: !!props.type,
    fetchPolicy: "cache-and-network",
  });
  const {
    loading: loadingTypes,
    error: errorTypes,
    data: typeData,
    fetchMore: typeFetchMore,
  } = useQuery(POKEMONS_BY_TYPE, {
    variables: {
      type: props.type,
      limit: props.limit,
    },
    skip: !props.type,
    fetchPolicy: "cache-and-network",
  });
  if (error || errorTypes) {
    return <div>Sorry, something broke off. Reload the app to try again!</div>;
  }
  if (genericData) {
    return (
      <>
        {genericData && genericData.pokemons.edges.length === 0 ? (
          <div className="noPokemons">
            There are no pokemons matching your preferences! <br />
            Please try another configuration.
          </div>
        ) : (
          <List
            loading={!!loading}
            loadMore={() =>
              genericFetchMore({
                variables: {
                  cursor: genericData.pageInfo.endCursor,
                },
                updateQuery: (
                  previousResult,
                  {
                    fetchMoreResult,
                  }: { fetchMoreResult: [PokemonNode<PokemonItem>] }
                ) => {
                  const newEdges = fetchMoreResult.pokemons.edges;
                  const pageInfo = fetchMoreResult.pokemons.pageInfo;

                  return newEdges.length
                    ? {
                        // Put the new comments at the end of the list and update `pageInfo`
                        // so we have the new `endCursor` and `hasNextPage` values
                        comments: {
                          __typename: previousResult.pokemons.__typename,
                          edges: [
                            ...previousResult.pokemons.edges,
                            ...newEdges,
                          ],
                          pageInfo,
                        },
                      }
                    : previousResult;
                },
              })
            }
            dataSource={genericData.pokemons.edges}
            pagination={{ position: "top", pageSize: props.pageQuantity }}
            renderItem={(item: PokemonNode<PokemonItem>) => (
              <PokemonListItem node={item.node} key={Number(item.node.id)} />
            )}></List>
        )}
      </>
    );
  } else if (typeData) {
    return (
      <>
        {typeData && typeData.pokemonsByType.edges.length === 0 ? (
          <div className="noPokemons">
            There are no pokemons matching your preferences! <br />
            Please try another configuration.
          </div>
        ) : (
          <List
            loading={!!loadingTypes}
            dataSource={typeData.pokemonsByType.edges}
            loadMore="ciao"
            pagination={{ position: "top", pageSize: props.pageQuantity }}
            renderItem={(item: PokemonNode<PokemonItem>) => (
              <PokemonListItem node={item.node} key={Number(item.node.id)} />
            )}></List>
        )}
      </>
    );
  } else {
    return (
      <div className="noPokemons">
        There are no pokemons matching your preferences! <br />
        Please try another configuration.
      </div>
    );
  }
};
