import React, { useState } from "react";
import { Form, Input } from "antd";

export default (props: { setType: Function; setName: Function }) => {
  return (
    <Form
      className="Options"
      layout="inline"
      onFieldsChange={(values) => {
        console.log(values);
      }}>
      <Form.Item
        label="Search by name"
        style={{ marginBottom: 0, marginRight: "20px" }}>
        <Input
          placeholder="Name"
          onChange={(e) => {
            props.setName(e.target.value);
          }}></Input>
      </Form.Item>
      <Form.Item label="Search by type" style={{ marginBottom: 0 }}>
        <Input
          placeholder="Type"
          onChange={(e) => {
            props.setType(e.target.value);
          }}></Input>
      </Form.Item>
    </Form>
  );
};
