import React, { useState } from "react";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";
import ListPageInterface from "../presentational/ListPageInterface";

const [pokeLimit, setPokeLimit] = useState(15);

const ALL_POKEMONS = gql`
  query pokemons {
    pokemons(limit: ${pokeLimit}) {
      edges {
        node {
          id
          name
          types
        }
      }
      pageInfo {
        endCursor
        hasNextPage
      }
    }
  }
`;

export default () => {
  const { loading, error, data } = useQuery(ALL_POKEMONS);
  console.log(loading, error, data);
  return <ListPageInterface />;
};
