import React from "react";
import { Form, Input, InputNumber, AutoComplete, Typography } from "antd";
import CustomLabel from "../presentational/InputLabel";

const pokemonTypes: string[] = [
  "fire",
  "water",
  "grass",
  "poison",
  "flying",
  "dragon",
  "psychic",
  "steel",
  "normal",
  "rock",
  "ice",
];

export default (props: {
  setType: Function;
  setName: Function;
  setLimit: Function;
  setQuantity: Function;
  pageQuantity: number;
  limit: number;
  name: String;
  type: String;
}) => {
  return (
    <Form
      className="Options"
      layout="inline"
      onFieldsChange={(values) => {
        console.log(values);
      }}>
      <Form.Item
        label="Pokemons per load"
        style={{ marginBottom: 0, marginRight: "20px" }}>
        <InputNumber
          defaultValue={props.limit}
          placeholder="Limit"
          min={1}
          onChange={(e) => {
            props.setLimit(e);
          }}></InputNumber>
      </Form.Item>
      <Form.Item
        label={<Typography.Text disabled={!!props.type}>name</Typography.Text>}
        style={{
          marginBottom: 0,
          marginRight: "20px",
        }}
        className={`${
          props.name && !props.type ? "greenInput" : "blackInput"
        }`}>
        <Input
          placeholder="Name"
          onChange={(e) => {
            props.setName(e.target.value);
            e.preventDefault();
          }}></Input>
      </Form.Item>
      <Form.Item
        label={
          <CustomLabel
            name={!!props.name}
            field="name"
            type={!!props.type}></CustomLabel>
        }
        style={{ marginBottom: 0 }}>
        <AutoComplete dataSource={pokemonTypes}>
          <Input
            placeholder="Type"
            onChange={(e) => {
              props.setType(e.target.value);
              e.preventDefault();
            }}></Input>
        </AutoComplete>
      </Form.Item>
    </Form>
  );
};
