import React from "react";
import { List } from "antd";
import { PokemonNode, PokemonItem } from "../../typescript/interfaces";
import PokemonListItem from "./PokemonListItem";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";

const ALL_POKEMONS = gql`
  query Pokemons($name: String, $after: ID, $limit: Int) {
    pokemons(q: $name, after: $after, limit: $limit) {
      edges {
        node {
          id
          name
          types
          classification
        }
      }
      pageInfo {
        endCursor
        hasNextPage
      }
    }
  }
`;

export default (props: {
  name: string;
  type: string;
  limit: number;
  pageQuantity: number;
}) => {
  const { loading, error, data } = useQuery(ALL_POKEMONS, {
    variables: {
      name: props.name,
      type: props.type,
      limit: props.limit,
    },
    fetchPolicy: "cache-and-network",
  });
  if (error) {
    return <div>Sorry, something broke off. Reload the app to try again!</div>;
  }
  return (
    <>
      {data && data.pokemons.edges.length === 0 ? (
        <div className="noPokemons">
          There are no pokemons matching your preferences! <br />
          Please try another configuration.
        </div>
      ) : (
        data && (
          <List
            dataSource={data.pokemons.edges}
            pagination={{ position: "top", pageSize: props.pageQuantity }}
            renderItem={(item: PokemonNode<PokemonItem>) => (
              <PokemonListItem node={item.node} key={Number(item.node.id)} />
            )}></List>
        )
      )}
    </>
  );
};
